// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
}

contract ERC20Airdrop {

    event AirdropCreated(address indexed tokenAddress, address indexed sender, address[] indexed recipients, uint256 amount);

    function createAirdrop(address tokenAddress, address[] memory recipients, uint256 amount) external {
        IERC20 token = IERC20(tokenAddress);
        uint256 totalAmount = recipients.length * amount;
        require(token.balanceOf(msg.sender) >= totalAmount, "Insufficient balance for airdrop");
        token.approve(address(this), totalAmount);
        for (uint256 i = 0; i < recipients.length; i++) {
            require(token.transferFrom(msg.sender, recipients[i], amount), "Token transfer failed");
        }
        emit AirdropCreated(tokenAddress, msg.sender, recipients, amount);
    }

    function withdraw(address tokenAddress) external {
        IERC20 token = IERC20(tokenAddress);
        token.transfer(msg.sender, token.balanceOf(address(this)));
    }
}
